import React, { useState, useEffect } from "react";
import axios from 'axios';
import Card from "react-bootstrap/Card";
import Button from "react-bootstrap/Button";
import Modal from "react-bootstrap/Modal";
import '../App.css';

const SideBar = (colors) => {
    const[show, setShow] = useState(false);
    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    const [randomColor] = useState([]);



    useEffect((colors) => {

        const index = Math.ceil(Math.random());

        const fetchColors = async () => {

          const res = await axios.get(`api/colors/${index}`);

          let randomColor = res.data;
          console.log(randomColor)
          return randomColor;

        };

        fetchColors();
    }, []);





  return (

            <div style={{backgroundColor: 'grey'}}>
            <Button variant="primary" className='randomColorButton' onClick={useEffect} onClick={handleShow}>Random Color</Button>


                <Modal show={show} onHide={handleClose}>
                    <Modal.Header closeButton>
                        <Modal.Title>{}</Modal.Title>
                    </Modal.Header>
                            <Card  style={{backgroundColor: randomColor.hex}}></Card>
                    <Modal.Footer>
                        <Button variant="secondary" onClick={handleClose}>
                            Close
                        </Button>
                    </Modal.Footer>
                </Modal>
            </div>

  );
};

export default SideBar
