import React, { useState, useEffect }  from 'react';
import Container from "../Container";

import axios from 'axios';
import ColorCard from ".//ColorCard";
import Row from "../Row";
import Column from "../Column";
import Wrapper from "./Wrapper";


const ColorSwatch = () => {
    const [colors, setColors] = useState([])
    const [loading, setLoading] = useState(false);
    const [currentPage, setCurrentPage] = useState(1);
    const [colorsPerPage, setColorsPerPage] = useState(12)


    useEffect(() => {
        const fetchColors = async () => {
          setLoading(true);
          const res = await axios.get('api/colors');
          setColors(res.data);
          setLoading(false);
        };

        fetchColors();
    }, []);


    // Get current colors
    const indexOfLastColor = currentPage * colorsPerPage;
    const indexOfFirstColor = indexOfLastColor - colorsPerPage;
    const currentColors = colors.slice(indexOfFirstColor, indexOfLastColor);

    // Change page
    const paginate = pageNumber => setCurrentPage(pageNumber);    


        return (
            <Wrapper>
                <Container>
                    <Row>

                        <Column size="md-3 sm-6">
                        <ColorCard
                            colors = {currentColors}
                            loading ={loading}
                            />
                        <Pagination
                            colorsPerPage={colorsPerPage}
                            totalColors={colors.length}
                            paginate={paginate}
                        />
                        </Column>

                    </Row>
                </Container>
            </Wrapper>
        )
}


export default ColorSwatch;
