import React, { useState, useEffect }  from 'react';
import Pagination from './Pagination'
import axios from 'axios';
import ColorCard from "./ColorCard";
import Wrapper from './Wrapper';
import Column from '../Column';
import Container from '../Container';
import Row from '../Row';



const ColorSwatch = () => {
    const [colors, setColors] = useState([])
    const [loading, setLoading] = useState(false);
    const [currentPage, setCurrentPage] = useState(1);
    const [colorsPerPage] = useState(12)


    useEffect(() => {
        const fetchColors = async () => {
          setLoading(true);
          const res = await axios.get('api/colors');
          setColors(res.data);
          setLoading(false);
        };

        fetchColors();
    }, []);


    // Get current colors
    const indexOfLastColor = currentPage * colorsPerPage;
    const indexOfFirstColor = indexOfLastColor - colorsPerPage;
    const currentColors = colors.slice(indexOfFirstColor, indexOfLastColor);

    // Change page
    const paginate = pageNumber => setCurrentPage(pageNumber);    


        return (
           <div>
           <div>
               <Wrapper>
                   <Container>
                    <ColorCard
                        colors = {currentColors}
                        loading ={loading}
                    /></Container>
                </Wrapper>
            </div>
            <div>
                    <Pagination
                        colorsPerPage={colorsPerPage}
                        totalColors={colors.length}
                        paginate={paginate}
                    />
            </div> 
            </div>  
        )
}


export default ColorSwatch;
